import React, { Component } from "react";
import MovieCard from "./MovieCard";

class DiscoverMovies extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: true,
      discover: {}
    };
  }

  async componentDidMount() {
    let res = await fetch("http://localhost:5000/discover/");
    this.setState({ discover: await res.json(), isLoading: false });
  }

  render() {
    return (
      <div>
        <p>Content: Discover page for new movies</p>
        <div>{this.state.isLoading ? (
            <h1>Loading...</h1>
          ) : <MovieCard title={this.state.discover[0].title} poster_path= {this.state.discover[0].poster_path}/>}</div>
      </div>
    );
  }
}

export default DiscoverMovies;
