import React, { Component } from "react";
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import Button from "react-bootstrap/Button";
//import MovieCard from "./MovieCard";

class DiscoverMovies extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: true,
      discover: {}
    };
  }

  async componentDidMount() {
    let res = await fetch("http://localhost:5000/discover/");
    this.setState({ discover: await res.json(), isLoading: false });
  }

  // mapMovieCards = () => {
  //   return this.state.discover.map(movie => <MovieCard key={movie.id} title={movie.title} overview={movie.overview} poster_path={movie.poster_path} />)
  // }

  mapMovieCards = () => {
    return (
      <div>
        <CardDeck>
            {this.state.discover.map((card) => (
              <Card style={{ width: "18rem" }}>
              <Card.Img variant="top" src={`https://image.tmdb.org/t/p/w500${props.poster_path}`}/>
              <Card.Body>
              <Card.Title>{props.title}</Card.Title>
              <Card.Text>{props.overview}</Card.Text>
              <Button variant="primary">Add</Button>
              </Card.Body>
              </Card>
            ))}
        </CardDeck>
      </div>
    );
  }

  render() {
    return (
      <div>
        <p>Content: Discover page for new movies</p>
        <div>{this.state.isLoading ? (
            <h1>Loading...</h1>
          ) : this.mapMovieCards()}</div>
      </div>
    );
  }
}

export default DiscoverMovies;




