import React, { Component } from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

export default class CreateMovie extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeOverview = this.onChangeOverview.bind(this);
    this.onChangeReleaseDate = this.onChangeReleaseDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',
      title: '',
      overview: '',
      release_date: new Date(),
      users: []
    }
  }

  componentDidMount() {
    this.setState({ 
      users: ['test user'],
      username: 'test user'
    });
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    });
  }

  onChangeTitle(e) {
    this.setState({
      description: e.target.value
    });
  }

  onChangeOverview(e) {
    this.setState({
      duration: e.target.value
    });
  }

  onChangeReleaseDate(date) {
    this.setState({
      date: date
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const movie = {
      username: this.state.username,
      title: this.state.description,
      overview: this.state.duration,
      release_date: this.state.date,
    };

    console.log(movie);

    window.location = '/';
  }

  render() {
    return (
      <div>
        <h3>Add New Movie</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group"> 
            <label>Username: </label>
            <select ref="userInput"
                required
                className="form-control"
                value={this.state.username}
                onChange={this.onChangeUsername}>
                {
                  this.state.users.map(function(user) {
                    return <option 
                      key={user}
                      value={user}>{user}
                      </option>;
                  })
                }
            </select>
          </div>
          <div className="form-group"> 
            <label>Title: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.title}
                onChange={this.onChangeTitle}
                />
          </div>
          <div className="form-group">
            <label>Overview: </label>
            <input 
                type="text" 
                className="form-control"
                value={this.state.overview}
                onChange={this.onChangeOverview}
                />
          </div>
          <div className="form-group">
            <label>Release Date: </label>
            <div>
              <DatePicker
                selected={this.state.release_date}
                onChange={this.onChangeReleaseDate}
              />
            </div>
          </div>

          <div className="form-group">
            <input type="submit" value="Add Movie" className="btn btn-primary" />
          </div>
        </form>
      </div>
    )
  }
}
